CREATE TABLE IF NOT EXISTS tutor(id INTEGER PRIMARY KEY AUTOINCREMENT, nome VARCHAR(100), email VARCHAR(100), pet_id INTEGER, FOREIGN KEY(pet_id) REFERENCES pet(id) ON DELETE CASCADE ON UPDATE NO ACTION);
CREATE TABLE IF NOT EXISTS pet(id INTEGER PRIMARY KEY AUTOINCREMENT, nome VARCHAR(100), raca VARCHAR(100), nascimento VARCHAR(15));
CREATE TABLE IF NOT EXISTS consulta(id INTEGER PRIMARY KEY AUTOINCREMENT, dia VARCHAR(10), horario VARCHAR(5), id_tutor INTEGER, id_pet integer, CONSTRAINT fk_tutor FOREIGN key (id_tutor) REFERENCES tutor, CONSTRAINT fk_pet FOREIGN Key (id_pet) REFERENCES pet ON DELETE CASCADE on UPDATE no ACTION);
